(573, 'Schlag', 'SC18', 1, 80, 0, 100, 0, 0, 0, 0, 0, 600, 0, 0, 1, 0, 0, 0, 0, 0, 0, 100, '!source spannt Îhre Faust an und versucht !target zu schlagen. !target wird getroffen und erleidet !type !damage Schaden.', '!source spannt Îhre Faust an und versucht !target zu schlagen. !target kann rechtzeitig ausweichen.', '!source spannt Îhre Faust an und versucht !target zu schlagen. !target wird getroffen, ist jedoch bereits tot.', '', '', 0, '', 0, 0, 0, 0, 0, 0, 0, 0, 10, 0, 0, 0, 1, 0, 1, ''),
(574, 'Tritt', 'TC18', 1, 85, 0, 100, 0, 0, 0, 0, 0, 800, 0, 0, 1, 0, 0, 0, 0, 0, 0, 100, '!source fliegt auf !target zu, um !target zu treten. !target wird vom Tritt getroffen und erleidet !type !damage Schaden.', '!source fliegt auf !target zu, um !target zu treten. !target konnte rechtzeitig ausweichen.', '!source fliegt auf !target zu, um !target zu treten. !target wird vom Tritt getroffen, ist jedoch bereits tot.', '', '', 0, '', 0, 0, 0, 0, 0, 0, 0, 0, 10, 0, 0, 0, 1, 0, 1, ''),
