INSERT INTO `verzeichnis` (`id`, `name`, `originalname`, `image`, `voiceactorger`, `voiceactorjap`, `universe`, `birthday`, `birthplace`, `height`, `weight`, `family`, `anime`, `manga`, `description`, `mainpage`) VALUES
(1, 'Son Goku', '孫 悟空', 'Goku', 'Corinna Dorenkamp, Frank Schaff, Tommy Morgenstern, Amadeus Strobel, Stefan Bräuler', 'Nozawa Masako', '7', '737', 'Vegeta', '185 CM', '62 KG', 'Vater: [ref=Bardock]Bardock[/ref]\r\nMutter: [ref=Gine]Gine[/ref]\r\nBruder: [ref=Radditz]Radditz[/ref]\r\nEhefrau: [ref=Chichi]Chichi[/ref]\r\nSohn: [ref=Son Gohan]Son Gohan[/ref], [ref=Son Goten]Son Goten[/ref]\r\nEnkeltochter: [ref=Pan]Pan[/ref]\r\nSchwiegertochter: [ref=Videl]Videl[/ref]\r\nSchwiegereltern: [ref=Rinderteufel]Rinderteufel[/ref]', 'Dragonball Episode 1', 'Band 1, Kapitel 1', 'Son Goku (Kakarot) ist ein Saiyajin welcher im Auftrag von [ref=Bardock]Bardock[/ref] seinem Vater und [ref=Gine]Gine[/ref] seiner Mutter auf die Erde geschickt wurde um diesen Planeten auszurotten. Er ist der  2. Sohn von [ref=Bardock]Bardock[/ref] und Bruder von [ref=Radditz]Radditz[/ref].\r\n\r\nBei der Landung auf der Erde wird er von seinem späteren Adoptivgroßvater [ref=Gohan]Son Gohan[/ref] gefunden und großgezogen. Durch einen Unfall stürzte Son Goku auf seinen Kopf, wodurch er sein Gedächtnis verlor und den Auftrag vergaß.\r\n\r\nSon Goku ist der Protagonist dieser Anime/Manga Reihe.', 1),
(2, 'Mr. Satan', 'ミスター・サタン', 'mrsatan', 'Elmar Gutmann', 'Daisuke Gōri', '7', '736', 'Erde', '188 CM', '94 KG', 'Ehemalige Frau: [ref=]Miguel[/ref]\r\nTochter: [ref=Videl]Videl[/ref]\r\nEnkeltochter: [ref=Pan]Pan[/ref]\r\nSchwiegersohn: [ref=Son Gohan]Son Gohan[/ref]', 'Dragonball Z, Episode 173', 'Band 33, Kapitel 393', '[b]Mr. Satan[/b] ist ein Mensch der sich selber als \"Der Champion\" betitelt und tritt zum erstmal in Erscheinung als feststand dass der Gewinner vom 23. Tenkaichi Budokai [ref=Son Goku]Son Goku[/ref] ist.\r\n\r\nNach den [ref=Cell]Cell[/ref] Spielen wurde [b]Mr. Satan[/b] als großer Held gefeiert, die Stadt wurde sogar unbenannt in Satan City, sie hieß vorher Orange Star City.\r\n\r\n[b]Mr. Satan[/b] ist der Vater von [ref=Videl]Videl[/ref] und Großvater von [ref=Pan]Pan[/ref], zudem ist er der beste Freund von [ref=Boo]Boo[/ref].', 1),
(3, 'Muten Roshi', '武天老師', 'mutenroshi', 'Heinz Ostermann, Karl Schulz', 'Kouhei Miyauchi ', '7', '430', 'Erde', '165 CM', '44 KG', 'Schwester: [ref=Uranai Baba]Uranai Baba[/ref]', 'Dragonball, Episode 3', 'Band 1, Kapitel 3', '[b]Muten Roshi[/b] ist ein Mensch und auch bekannt als \"Herr der Schildkröten\".\r\n\r\nEr ist Leiter der Schildkrötenschule und bildete unter anderem [ref=Son Goku]Son Goku[/ref], [ref=Yamchu]Yamchu[/ref] und [ref=Krillin]Krillin[/ref] aus.\r\n\r\n[b]Muten Roshi[/b] gilt als Erfinder des \"Kamehame Ha\", zudem ist er der Bruder von [ref=Uranai Baba]Uranai Baba[/ref].\r\n\r\n', 1),
(4, 'Vegeta', 'ベジータ', 'Vegeta', 'Santiago Ziesmer, Oliver Siebeck', 'Ryō Horikawa', '7', '732', 'Vegeta', '165 CM', '63 KG', 'Vater: [ref=König Vegeta]König Vegeta[/ref]\r\nBruder: [ref=Tarble]Tarble[/ref]\r\nEhefrau: [ref=Bulma]Bulma[/ref]\r\nSohn: [ref=Trunks]Trunks[/ref]\r\nTochter: [ref=Bra]Bra[/ref]\r\nSchwiegervater: [ref=Dr. Brief]Dr. Brief[/ref]\r\nSchwiegermutter: [ref=Panchy]Panchy[/ref]\r\nSchwägerin: [ref=Tights]Tights[/ref], [ref=Gure]Gure[/ref]\r\n', 'Dragonball Z, Episode 5', 'Band 17, Kapitel 204', 'Er ist ein Saiyajin welcher auf dem Planeten Vegeta geboren wurde.\r\n\r\nAls Sohn von [ref=König Vegeta]König Vegeta[/ref] trägt er den Titel \r\n\"Prinz der Saiyajin\", welchen er mit großen Stolz trägt.\r\n\r\nDurch die Hand von [ref=Freezer]Freezer[/ref] verlor [b]Vegeta[/b] seinen Heimatplaneten. Er schloss sich ihm an um später Rache zu nehmen.\r\n\r\n\r\n', 1),
(5, 'Bardock', 'バーダック', 'bardock', 'Tommy Morgenstern, Björn Schalla', 'Masako Nozawa', '7', 'Unbekannt', 'Vegeta', 'Unbekannt', 'Unbekannt', 'Ehefrau: [ref=Gine]Gine[/ref]\r\nSohn: [ref=Radditz]Raddtiz[/ref], [ref=Son Goku]Son Goku[/ref]\r\nEnkel: [ref=Son Gohan]Son Gohan[/ref], [ref=Son Goten]Son Goten[/ref], [ref=Pan]Pan[/ref]\r\nSchwiegertochter: [ref=Chichi]Chichi[/ref]\r\n', 'Unbekannt', 'Band 26, Kapitel 307', '[b]Bardock[/b] war ein stolzer Krieger der Saiyajin. \r\n\r\n[b]Bardock[/b] ist der Saiyajin welchen man mit der Legende vom Super Saiyajin in verbindung bringt, man behauptet er wäre der erste Saiyajin gewesen der die Verwandlung gemeistert hat. \r\n\r\nEr ist der Vater von [ref=Radditz]Radditz[/ref] und [ref=Son Goku]Kakarot (Son Goku)[/ref] sowie Ehemann von [ref=Gine]Gine[/ref] welche die Mutter der beiden Kinder ist.\r\n\r\n[b]Bardock[/b] starb bei der Verteidigung seines Heimatplaneten Vegeta als [ref=Freezer]Freezer[/ref] diesen angriff.\r\n\r\nIm Anime selber erhielt [b]Bardock[/b] keinen Auftritt, dafür bekam er aber 2 Filme:\r\n\r\n[b]Bardock[/b] - [ref=Son Goku]Son Gokus[/ref] Vater\r\n\r\nEpisode of [b]Bardock[/b]\r\n\r\n', 1),
(6, 'Gine', 'ギネ', 'gine', 'Marieke Oeffinger', 'Naoko Watanabe', '7', 'Unbekannt', 'Vegeta', 'Unbekannt', 'Unbekannt', 'Ehemann: [ref=Bardock]Bardock[/ref]\r\nSohn: [ref=Radditz]Radditz[/ref], [ref=Son Goku]Son Goku[/ref]\r\nEnkel: [ref=Son Gohan]Son Gohan[/ref], [ref=Son Goten]Son Goten[/ref], [ref=Pan]Pan[/ref]\r\nSchwiegertochter: [ref=Chichi]Chichi[/ref]', 'Unbekannt', 'Unbekannt ', '[b]Gine[/b] ist eine stolze weibliche Kriegerin der Saiyajin Rasse.\r\n\r\nSie ist die Mutter von [ref=Radditz]Radditz[/ref] und [ref=Son Goku]Kakarot (Son Goku)[/ref] sowie die Ehefrau von [ref=Bardock]Bardock[/ref].\r\n\r\nIm Anime und Manga bekam [b]Gine[/b] keinen Auftritt, jedoch kam sie in dem Film \"Dragonball Super: [ref=Broly]Broly[/ref]\" vor.', 1),
(7, 'Chichi', 'チチ', 'chichi', 'Ilya Welter, Julia Ziffer', 'Shō Mayumi, Watanabe Naoko', '7', 'Unbekannt', 'Erde', '163 CM', '50 KG', 'Vater: [ref=Rinderteufel]Rinderteufel[/ref]\r\nEhemann: [ref=Son Goku]Son Goku[/ref]\r\nSohn: [ref=]Son Gohan[/ref], [ref=Son Goten]Son Goten[/ref]\r\nEnkel: [ref=Pan]Pan[/ref]\r\nSchwiegervater: [ref=Bardock]Bardock[/ref]\r\nSchwiegermutter: [ref=Gine]Gine[/ref]\r\nSchwager: [ref=Radditz]Radditz[/ref]\r\nSchwiegertochter: [ref=Videl]Videl[/ref]\r\n\r\n', 'Dragonball, Episode 7', 'Band 1, Kapitel 11', '[b]Chichi[/b] ist ein Mensch und die Tochter des [ref=Rinderteufel]Rinderteufels[/ref], man nennt sie auch die \"Prinzessin des Bratpfannenbergs\"\r\n\r\nSie ist die Ehefrau von [ref=Son Goku]Son Goku[/ref] und Mutter der beiden Söhne [ref=Son Gohan]Son Gohan[/ref] sowie [ref=Son Goten]Son Goten[/ref].\r\n\r\n[b]Chichi[/b] ist eine sehr strenge Mutter, die sich für Ihre Kinder ein normales Leben wünscht, abseits von Krieg und Kämpfen wie sie ihr Ehemann [ref=Son Goku]Son Goku[/ref] bestreitet um die Erde von bösen Kreaturen zu befreien.\r\n\r\n', 1),
(8, 'Son Gohan', '孫 悟飯', 'songohan', 'Sandro Blümel, Robin Kahnmayer', 'Masako Nozawa', '7', '18.05.757', 'Erde', '176 CM', '61 KG', 'Vater: [ref=Son Goku]Son Goku[/ref] \r\nMutter: [ref=Chichi]Chichi[/ref]\r\nBruder: [ref=Son Goten]Son Goten[/ref]\r\nEhefrau: [ref=Videl]Videl[/ref]\r\nTochter: [ref=Pan]Pan[/ref]\r\nGroßvater: [ref=Bardock]Bardock[/ref], [ref=Rinderteufel]Rinderteufel[/ref]\r\nGroßmutter: [ref=]Gine[/ref]\r\nOnkel: [ref=Radditz]Radditz[/ref]\r\nSchwiegervater: [ref=Mr. Satan]Mr. Satan[/ref]\r\nSchwiegermutter: [ref=Miguel]Miguel[/ref]\r\n', 'Dragonball Z, Episode 1', 'Band 17, Kapitel 196', '[b]Son Gohan[/b] ist ein Halb-Saiyajin, Sohn von (Saiyajin) [ref=Son Goku]Son Goku[/ref] und (Mensch) [ref=Chichi]Chichi[/ref].\r\n\r\nEr ist an sich ein sehr friedlicher Charakter, welcher mit zunehmenden Alter das Kämpfen sehr vernachlässigt.\r\n\r\n[b]Son Gohan[/b] hat in jungen Jahren eher die Charakterzüge seines Vaters Son Goku, doch später geht er dem Wunsch seiner Mutter nach und führt ein normales Leben.\r\n\r\n\r\n\r\n', 1),
(9, 'Radditz', 'ラディッツ', 'radditz', 'Tobias Kluckert', 'Chiba Shigeru', '7', '12.10.761', 'Vegeta', 'Unbekannt', 'Unbekannt', 'Vater: [ref=Bardock]Bardock[/ref]\r\nMutter: [ref=Gine]Gine[/ref]\r\nBruder: [ref=Son Goku]Son Goku[/ref]\r\nNeffe: [ref=Son Gohan]Son Gohan[/ref], [ref=Son Goten]Son Goten[/ref]\r\nSchwägerin: [ref=Chichi]Chichi[/ref]\r\nNichte: [ref=Pan]Pan[/ref]', 'Dragonball Z, Episode 1', 'Band 15, Kapitel 176', '[b]Radditz[/b] ist wie sein jüngerer Bruder [ref=Son Goku]Son Goku[/ref] ein Saiyajin welcher auf dem Planeten Vegeta geboren wurde.\r\n\r\nAls er auf der Erde ankam war [b]Radditz[/b] verwundert darüber dass die Erde noch nicht entvölkert wurde.\r\n\r\nEr suchte nach [ref=Son Goku]Son Goku[/ref] damit er ihnen bei der Eroberung eines Planeten unterstützt.\r\n\r\nIhm war nicht bewusst das [ref=Son Goku]Son Goku[/ref] durch einen Sturz auf den Kopf sein Gedächtnis verloren hatte, wodurch er auch seinen Auftrag vergaß weshalb man ihn überhaupt auf die Erde geschickt hat.\r\n\r\nErst durch [b]Radditz[/b] erfuhr [ref=Son Goku]Son Goku[/ref] genauere Informationen zu seiner Vergangenheit und von wem er abstammt.', 0),
(10, 'Rinderteufel', '牛魔王', 'rinderteufel', ' Reinhard Schulat-Rademache, Jan Spitzer', 'Gōri Daisuke', '7', '06.05.697', 'Erde', '3,02 Meter', '265 Kg', 'Tochter: [ref=Chichi]Chichi[/ref]\r\nSchwiegersohn: [ref=Son Goku]Son Goku[/ref]\r\nEnkel: [ref=Son Gohan]Son Gohan[/ref], [ref=Son Goten]Son Goten[/ref], [ref=Pan]Pan[/ref]', 'Dragonball, Episode 7', 'Band 1, Kapitel 11', 'Der [b]Rinderteufel[/b] oder \"Der Herrscher über den Bratpfannenberg\" wie er auch genannt wird ist ein Erdling und Vater von [ref=Chichi]Chichi[/ref], Schwiegervater von [ref=Son Goku]Son Goku[/ref] sowie Großvater seiner Enkel [ref=Son Gohan]Son Gohan[/ref], [ref=Son Goten]Son Goten[/ref] und [ref=Pan]Pan[/ref].\r\n\r\n', 0),
(11, 'Pan', 'パン', 'pan', 'Adak Azdasht', 'Yūko Minaguchi, Shino Kakinuma', '779', 'Jahr 779', 'Erde', '99 CM', '14 Kg', 'Mutter: [ref=Videl]Videl[/ref]\r\nVater: [ref=Son Gohan]Son Gohan[/ref]\r\nGroßvater: [ref=Son Goku]Son Goku[/ref], [ref=Mr. Satan]Mr. Satan[/ref]\r\nGroßmutter: [ref=Chichi]Chichi[/ref], [ref=Miguel]Miguel[/ref]\r\nOnkel: [ref=Son Goten]Son Goten[/ref]\r\nUr-Großvater: [ref=Bardock]Bardock[/ref], [ref=Rinderteufel]Rinderteufel[/ref]\r\nUr-Großmutter: [ref=Gine]Gine[/ref]\r\nGroßonkel: [ref=Radditz]Radditz[/ref]', 'Dragonball Z, Episode 288', 'Band 42, Kapitel 510', '[b]Pan[/b] ist wie ihr Vater [ref=Son Gohan]Son Gohan[/ref] ein Halb-Saiyajin, obwohl man bei ihr sagt sie wäre zu 3/4 Mensch und nur zu 1/4 Saiyajin.\r\n\r\nSie ist die Enkeltochter von [ref=Son Goku]Son Goku[/ref] und [ref=Chichi]Chichi[/ref] sowie Enkelin des [ref=Rinderteufel]Rinderteufels[/ref].\r\n\r\n[b]Pan[/b] hatte schon als kleines Kind eine beachtliche Kampfkraft von [b]15.000.000.000[/b]', 0),
(12, 'Son Goten', '孫悟天', 'songoten', 'Ricardo Richter, Ozan Ünal', 'Masako Nozawa', '7', 'Jahr 767', 'Erde', '1,83 Meter', '60 Kg', 'Mutter: [ref=Chichi]Chichi[/ref]\r\nVater: [ref=Son Goku]Son Goku[/ref]\r\nBruder: [ref=Son Gohan]Son Gohan[/ref]\r\nGroßvater: [ref=Bardock]Bardock[/ref], [ref=Rinderteufel]Rinderteufel[/ref]\r\nGroßmutter: [ref=Gine]Gine[/ref]\r\nOnkel: [ref=Radditz]Radditz[/ref]\r\nNichte: [ref=Pan]Pan[/ref]\r\n', 'Dragonball Z, Episode 201', 'Band 36, Kapitel 424', '[b]Son Goten[/b] stammt wie sein Bruder [ref=Son Gohan]Son Gohan[/ref] von seiner Menschlichen Mutter [ref=Chichi]Chichi[/ref] sowie seinem Saiyajin Vater [ref=Son Goku]Son Goku[/ref] ab, weshalb man ihn zu den Halb-Saiyajin zählt.\r\n\r\n[b]Son Goten[/b] meisterte bereits schon im Kindesalter die Verwandlung zum Super Saiyajin.', 0),
(13, 'Videl', 'ビーデル', 'videl', 'Anna Carlsson', 'Yuko Minaguchi', '7', 'Jahr 757', 'Erde', '1,57 Meter', '47 Kg', 'Vater: [ref=Mr. Satan]Mr. Satan[/ref]\r\nMutter: [ref=Miguel]Miguel[/ref]\r\nEhemann: [ref=Son Gohan]Son Gohan[/ref]\r\nTochter: [ref=Pan]Pan[/ref]\r\nSchwiegervater: [ref=Son Goku]Son Goku[/ref]\r\nSchwiegermutter: [ref=Chichi]Chichi[/ref]\r\nSchwager: [ref=Son Goten]Son Goten[/ref]\r\n', 'Dragonball Z, Episode 227', 'Band 36, Kapitel 421', '[b]Videl[/b] ist die Ehefrau von [ref=Son Gohan]Son Gohan[/ref] und Tochter des selbsternannten \"Champions\" [ref=Mr. Satan]Mr. Satan[/ref].\r\n\r\nAls Tochter von [ref=Mr. Satan]Mr. Satan[/ref] hatte [ref=Videl]Videl[/ref] schon in jungen Jahren das Kämpfen für sich entdeckt, dieses Talent setzt sie ein um an der Seite von [ref=Son Gohan]Son Gohan[/ref] als \"Saiyaman\"  Verbrecher zu bekämpfen.\r\n\r\n', 0),
(14, 'Dr. Brief', 'ブリーフ博士', 'brief', 'Katja Liebing, Claudia Urbschadt-Mingues', 'Hiromi Tsuru', '7', '08.05.774', 'Erde', '165', 'Unbekannt', 'Ehefrau: [ref=Panchy]Panchy[/ref]\r\nTochter: [ref=Bulma]Bulma[/ref], [ref=Tights]Tights[/ref]\r\nSchwiegersohn: [ref=Vegeta]Vegeta[/ref]\r\nEnkel: [ref=Trunks]Trunks[/ref], [ref=Bulla]Bulla[/ref]\r\n ', 'Unbekannt', 'Unbekannt', '[b]Dr. Brief[/b] oder [b]Mr. Brief[/b] wie er auch genannt wird, ist der Vater von [ref=Bulma]Bulma[/ref] und Präsident der Capsule Corporation.\r\n\r\n[b]Dr. Brief[/b] gilt als einer der bekanntesten, intelligentesten und reichsten Persönlichkeiten auf dem Planeten Erde, zusätzlich war er die Person die das Raumschiff baute womit [ref=Son Goku]Son Goku[/ref] nach Namek flog.', 0),
(15, 'Bulma', 'ブルマ', 'bulma', 'Katja Liebing, Claudia Urbschadt-Mingues', 'Hiromi Tsuru', '7', '18.08.733', 'Erde', '165', '50', 'Vater: [ref=Dr. Brief]Dr. Brief[/ref]\r\nMutter: [ref=Panchy]Panchy[/ref]\r\nSchwiegervater: [ref=König Vegeta]König Vegeta[/ref]\r\nEhemann: [ref=Vegeta]Vegeta[/ref]\r\nSchwager: [ref=Tarble]Tarble[/ref]\r\nSchwägerin: [ref=Gure]Gure[/ref]\r\nSchwester: [ref=Tights]Tights[/ref]\r\nSohn: [ref=Trunks]Trunks[/ref]\r\nTochter: [ref=Bra]Bra[/ref]', 'Dragonball, Episode 1', 'Band 1, Kapitel 1', '[b]Bulma[/b] ist eine sehr intelligente Wissenschaftlerin, Tochter von [ref=Dr. Brief]Dr. Brief[/ref] und Ehefrau von [ref=Vegeta]Vegeta[/ref].\r\n\r\nSie ist eine sehr aufbrausende Persönlichkeit, zudem war sie der erste Kontakt von [ref=Son Goku]Son Goku[/ref] nachdem sein \"Vater\" [ref=Gohan]Gohan[/ref] gestorben war.\r\n\r\nSie half [ref=Son Goku]Son Goku[/ref] dabei die Dragonballs zu finden.\r\n\r\nIn den ganzen Jahren begleitet sie [ref=Vegeta]Vegeta[/ref] und [ref=Son Goku]Son Goku[/ref] immer noch auf ihren Abenteuern und unterstützt sie mit Ihren Erfindungen.', 0),
(16, 'Gure', 'グレ', 'gure', 'Unbekannt', 'Unbekannt', '7', 'Unbekannt', 'Unbekannt', 'Unbekannt', 'Unbekannt', 'Ehemann: [ref=Tarble]Tarble[/ref]\r\nSchwiegervater: [ref=König Vegeta]König Vegeta[/ref]\r\nSchwager: [ref=Vegeta]Vegeta[/ref]\r\nSchwägerin: [ref=Bulma]Bulma[/ref]\r\nNeffe: [ref=Trunks]Trunks[/ref]\r\nNichte: [ref=Bulla]Bulla[/ref]', 'Unbekannt', 'Unbekannt', '[b]Gure[/b] ist die Ehefrau von [ref=Tarble]Tarble[/ref], sie lernte Ihn kennen als er von seinem Vater [ref=König Vegeta]König Vegeta[/ref] auf ihren Planeten geschickt wurde.', 0),
(17, 'Krillin', 'クリリン', 'krillin', 'Norman Matt, Wanja Gerick', 'Mayumi Tanaka', '7', '', 'Erde', '153', '45', 'Ehefrau: [ref=C-18]C-18[/ref]\r\nTochter: [ref=Marron]Marron[/ref]\r\nSchwager: [ref=C-17]C-17[/ref]', 'Dragonball, Episode 14', 'Band 3, Kapitel 25', '[b]Krillin[/b] oder wie im Original genannt [b]Kuririn[/b], ist der beste Freund von [ref=Son Goku]Son Goku[/ref]\r\n\r\nEr ist der Ehemann von [ref=C-18]C-18[/ref] und Vater von seiner Tochter [ref=Marron]Marron[/ref]\r\n\r\nAls [b]Krillin[/b] auf Namek durch Freezer getötet wurde, gelang [ref=Son Goku]Son Goku[/ref] die Verwandlung zum Super Saiyajin.', 0),
(18, 'Yamcha', 'ヤムチャ', 'yamchu', 'Karlo Hackenberger', 'Toru Furuya', '7', '20.03.733', 'Erde', '183', '68', 'Begleiter: [ref=Pool]Pool[/ref]', 'Dragonball, Episode 5', 'Band 1, Kapitel 7', '[b]Yamcha[/b] ist ein ehemaliger Wüstenbandit und Ex-Freund von [ref=Bulma]Bulma[/ref].\r\n\r\n[b]Yamcha[/b] ist einer der besten Freunde von [ref=Son Goku]Son Goku[/ref] und Mitglied der Z-Gruppe.\r\n\r\nEr selber besitzt keine bekannten Familienmitglieder, sein Begleiter [ref=Pool]Pool[/ref] ist jedoch immer an seiner Seite gewesen und zählt aus dem Grund zur Familie dazu.\r\n\r\n[b]Yamcha[/b] ist ein sehr begabter Kämpfer, welcher mit seinen Fähigkeiten schon oft gegen Bedrohungen helfen konnte.\r\n\r\n\r\n\r\n', 1),
(19, 'Trunks', 'ト ラ ン ク ス', 'trunks', 'Arda Vural', 'Takeshi Kusao', '7', '08.05.774', 'Erde', '129 CM', '30 KG', 'Vater: [ref=Vegeta]Vegeta[/ref]\r\nMutter: [ref=Bulma]Bulma[/ref]\r\nSchwester: [ref=Bulla]Bulla[/ref]\r\nGroßeltern: [ref=Dr. Brief]Dr. Brief[/ref], [ref=Panchy]Panchy[/ref], [ref=König Vegeta]König Vegeta[/ref]\r\nTante: [ref=Tights]Tights[/ref], [ref=Gure]Gure[/ref]\r\nOnkel: [ref=Tarble]Tarble[/ref]\r\n', 'Dragonball Z, Folge 126', 'Band 28, Kapitel 337', '[b]Trunks[/b] ist ein Halb-Saiyajin welcher aus der Beziehung von [ref=Bulma]Bulma[/ref] und [ref=Vegeta]Vegeta[/ref] hervorging.\r\n\r\nEr ist der ältere Bruder von [ref=Bulla]Bulla[/ref] und bester Freund von [ref=Son Goten]Son Goten[/ref].\r\n\r\nEin besonderes Merkmal welches [b]Trunks[/b] trägt sind seinen lilanen Haare, für Saiyajins ist es üblich schwarze Haare zu haben.\r\n\r\n', 0),
(20, 'König Vegeta', 'ベジータ王', 'koenigvegeta', 'Andreas Thieck', 'Satō Masaharu', '7', 'Jahr 737', 'Vegeta', 'Unbekannt', 'Unbekannt', 'Sohn: [ref=Vegeta]Vegeta[/ref], [ref=Tarble]Tarble[/ref]\r\nEnkel: [ref=Trunks]Trunks[/ref], [ref=Bulla]Bulla[/ref]\r\nSchwiegertochter: [ref=Bulma]Bulma[/ref], [ref=Gure]Gure[/ref]  ', 'Dragonball Z, Episode 78', 'Band 1, Kapitel 3', '[b]König Vegeta[/b] ist wie sein Name schon verrät der König und Herrscher des Planeten Vegeta und somit auch König der Saiyajins.\r\n\r\nEr ist Vater von 2 Söhnen, [ref=Vegeta]Vegeta[/ref] und [ref=Tarble]Tarble[/ref].\r\n\r\n[b]König Vegeta[/b] leitete den Krieg gegen die Tsufurujins, nachdem er die Zusammenarbeit mit [ref=Freezer]Freezer[/ref] und seinem Gefolge beenden wollte, wurde dieser durch einen Schlag von [ref=Freezer]Freezer[/ref] getötet. \r\n\r\n\r\n', 0),
(21, 'Oberältester ', '最長老', 'namekaeltester', ' Jürgen Kluckert', 'Junpei Takiguchi', '7', 'Unbekannt', 'Namek', 'Unbekannt', 'Unbekannt', '[b]Unbekannt[/b]', 'Dragonball Z, Episode 53', 'Band 21, Kapitel 251', 'Der [b]Oberälteste[/b] wird von Freezer nach dessen Landung auf dem Planeten Namek für den Anführer der Namekianer gehalten.\r\n\r\nNachdem der [b]Oberälteste[/b] nach der Zerstörung von seinem Heimatplaneten auf der Erde starb, übernahm [ref=Moori]Moori[/ref] die Führung vom Volk.', 0),
(22, 'Nappa', 'ナ ッ パ', 'nappa', 'Gerald Paradies', 'Shouzou Iizuka', '7', 'Unbekannt', 'Vegeta', 'Unbekannt', 'Unbekannt', 'Unbekannt', 'Dragonball Z, Episode 5', 'Band 17, Kapitel 204', '[b]Nappa[/b] ist wie sein Kampfpartner [ref=Vegeta]Vegeta[/ref] auf dem Planeten Vegeta geboren und gehört so zur Rasse der Saiyajin.\r\n\r\nEr war wie [ref=Vegeta]Vegeta[/ref] und [ref=Radditz]Radditz[/ref] ein untergebener von [ref=Freezer]Freezer[/ref] nach dem dieser den Planeten Vegeta auslöschte.', 0),
(23, 'Dende', 'デンデ', 'dende', 'Raul Richter', 'Tomiko Suzuki', '7', '24.12.767', 'Namek', 'Unbekannt', 'Unbekannt', 'Vater: [ref=Oberältester]Oberältester[/ref]\r\nBruder: [ref=Moori]Moori[/ref], [ref=Nail]Nail[/ref], [ref=Tsuno]Tsuno[/ref]', 'Dragonball Z, Episode 46', 'Band 21, Kapitel 252', '[b]Dende[/b] ist ein junger Namekianer.\r\n\r\nEr ist sehr bekannt für seine heilenden Fähigkeiten, als [ref=Son Goku]Son Goku[/ref] und seine Freunde ihn retteten und nach der Zerstörung von Namek auf die Erde brachten, übernahm er später die Rolle vom [ref=Gott]Gott[/ref] der Erde.\r\n\r\n[b]Dende[/b] hat wie viele andere Namekianer die Fähigkeit Dragonballs zu erschaffen, als er [ref=Shenlong]Shenlong[/ref] wieder erweckte konnte dieser 2 Wünsche erfüllen an statt von 1.\r\n', 0),
(24, 'Panchy', 'パンチー', 'panchy', 'Angela Ringer', 'Youko Kawanami', '7', '08.05.774', 'Erde', 'Unbekannt', 'Unbekannt', 'Ehemann: [ref=Dr. Brief]Dr. Brief[/ref]\r\nTochter: [ref=Bulma]Bulma[/ref], [ref=Tights]Tights[/ref].\r\nEnkel: [ref=Trunks]Trunks[/ref], [ref=Bra]Bra[/ref], [ref=Bulla]Bulla[/ref]\r\nSchwiegersohn: [ref=Vegeta]Vegeta[/ref]\r\n', 'Dragonball, Episode 44', 'Band 6, Kapitel 69', '[b]Panchy[/b] ist die Ehefrau von [ref=Dr. Brief]Dr. Brief[/ref] sowie Mutter von [ref=Bulma]Bulma[/ref] und [ref=Tights]Tights[/ref].', 0),
(25, 'Piccolo', 'ピッコロジュニア', 'piccolo', 'David Nathan', 'Furukawa Toshio', '7', '09.05.953', 'Erde', '226 CM', '116 KG', 'Vater: [ref=Oberteufel Piccolo]Oberteufel Piccolo[/ref]', 'Dragonball, Episode 122', 'Band 14, Kapitel 161', '[b]Piccolo[/b] ist ein Namekianer des \"Dragon Clans\" und Sohn von [ref=Oberteufel Piccolo]Oberteufel Piccolo[/ref].\r\n\r\nEr wurde dafür ausgewählt den Sohn von [ref=Son Goku]Son Goku[/ref] zu trainieren und zwar handelt sich hierbei um sein 1. Geborenen, [ref=Son Gohan]Son Gohan[/ref].\r\n\r\nEr hat durch das Training mit [ref=Son Gohan]Son Gohan[/ref] eine sehr starke Bindung zu ihm aufbauen können welche man immer wieder spürt.\r\n\r\n[b]Piccolo[/b] fusionierte mit [ref=Gott]Gott[/ref] und zählte seitdem als die \"Wiedervereinigung des Namenlosen Namekianer\".\r\n\r\n\r\n\r\n', 0),
(26, 'Uranai Baba', '占う ら ない バ バ', 'uranaibaba', 'Ulrike Lau', 'Junpei Takiguchi', '7', 'Unbekannt', 'Erde', 'Unbekannt', 'Unbekannt', 'Burder: [ref=Muten Roshi]Muten Roshi[/ref]', 'Dragonball, Episode 61', 'Band 9, Kapitel 98', '[b]Uranai Baba[/b] ist eine alte Hexe sowie ältere Schwester vom [ref=Muten Roshi]Herrn der Schildkröten[/ref].\r\n\r\nMit ihrer Kugel worauf sie sitzt ist es [b]Uranai Baba[/b] möglich andere Lebewesen zu beobachten.\r\n\r\n[b]Uranai Baba[/b] besitzt die Fähigkeit dank eines Vertrages mit [ref=Enma]Enma[/ref], einen toden für einen Tag die Lebensenergie zurück zu schenken und wieder lebendig zu machen.', 0),
(27, 'Tarble', 'タ ー ブ ル', 'tarble', 'Unbekannt', 'Morita Masakazu', '7', 'Unbekannt', 'Vegeta', 'Unbekannt', 'Unbekannt', 'Vater: [ref=König Vegeta]König Vegeta[/ref]\r\nBruder: [ref=Vegeta]Vegeta[/ref]\r\nEhefrau: [ref=Gure]Gure[/ref]\r\nNeffe: [ref=Trunks]Trunks[/ref]\r\nNichte: [ref=Bulla]Bulla[/ref]\r\nSchwägerin: [ref=Bulma]Bulma[/ref]', 'Unbekannt', 'Unbekannt', '[b]Tarble[/b] ist ein Saiyajin vom Planeten Vegeta, er ist der Sohn von [ref=König Vegeta]König Vegeta[/ref] und Bruder von [ref=Vegeta]Prinz Vegeta[/ref].\r\n\r\n[b]Tarble[/b] wurde als Baby bereits von seinem [ref=König Vegeta]Vater[/ref] vor der Zerstörung seines Heimatplaneten auf einen weit entfernten Planeten geschickt wo er dann auch seine jetzige Ehefrau [ref=Gure]Gure[/ref] kennlernte.', 0),
(28, 'Yajirobe', 'ヤ ジ ロ ベ ー', 'yajirobi', 'Eberhard Prüter', 'Mayumi Tanaka', '7', '08.05.774', 'Erde', '165 CM', '78 KG', 'Unbekannt', 'Dragonball, Episode 104', 'Band 12, Kapitel 138', '[b]Yajirobe[/b] ist ein Bewohner des Planeten Erde und ein sehr guter Freund von [ref=Korin]Meister Quitte[/ref] wo er auch die meiste seiner Zeit verbringt.\r\n\r\n[b]Yajirobe[/b] ist ein Ronin, dies bedeutet dass er ein Meisterloser Samurai ist.\r\n\r\nEr ist zwar sehr dick, davon sollte man sich aber nicht täuschen lassen! Mit seinem Schwert stellt sich [b]Yajirobe[/b] sehr geschickt an.\r\n\r\n\r\n\r\n', 0),
(29, 'Freezer', 'フ リ ー ザ', 'freezer', 'Thomas Nero Wolff', 'Ryusei Nakao', '7', 'Unbekannt', 'Freezer 79', '158 CM', 'Unbekannt', 'Vater: [ref=King Cold]King Cold[/ref]\r\nBruder: [ref=Cooler]Cooler[/ref]', 'Dragonball Z, Episode 45', 'Band 21, Kapitel 247', '[b]Freezer[/b] ist eine außerirdische Lebensform, als \"Changeling\"/\"Wandler\" ist es ihm möglich durch das ändern der Äußerlichkeit seine Kampfkraft zu steigern.\r\n\r\nEr ist der Sohn von [ref=King Cold]King Cold[/ref] und Bruder von [ref=Cooler]Cooler[/ref].\r\n\r\n[b]Freezer[/b] war für die Zerstörung vom Planeten Vegeta und beinahe vollkommene Ausrottung der Saiyajin verantwortlich. \r\n\r\n\r\n\r\n', 0);
